import React from "react";
import PropTypes from "prop-types";
import StoryMedia from "./StoryMedia";

const Story = props => {
  const { story, storyClass } = props;

  const renderSpeakers = speakers => {
    return (
      <div key={`${story.id}-speakers`}>
{/*         {
          speakers.map(speaker => {
            return (
              <img
                src={speaker.picture_url}
                alt={speaker.name}
                title={speaker.name}
                key={speaker.id}
              />
            );
          })
        }
 */}        <p style={{ fontWeight: 'bold' }}>
          {
            speakers.map(speaker => {
              return speaker.name
            }).join(', ')
          }
        </p>
      </div>
    );
  }

  return (
    <React.Fragment>
      <li
        className={storyClass}
        onClick={() => props.onStoryClick(story)}
        onKeyDown={() => props.onStoryClick(story)}
        key={story.id}
        role="presentation"
      >
        <div className="speakers">
          {renderSpeakers(story.speakers)}
        </div>
        <div className="container">
          <h6 className="title">
            {story.title}
            {story.permission_level === "restricted" && " ðŸ”’"}
          </h6>
          <p className="description" dangerouslySetInnerHTML={{ __html: story.desc }}></p>
          {
            story.media &&
            story.media.map(file => (
              <StoryMedia
                file={file}
                key={story.media.id}
              />
            ))
          }
          {
            story.language &&
            <p>
              <b>Language:</b> {story.language}
            </p>
          }
        </div>
      </li>
    </React.Fragment>
  );
}

Story.propTypes = {
  story: PropTypes.object,
  onStoryClick: PropTypes.func,
  storyClass: PropTypes.string,
};

Story.defaultProps = {
  story: {},
  onStoryClick: () => { },
  storyClass: "",
};

export default Story;
